sp_widget.client_script:
function($scope) {
  $scope.data = {
    qualifications: []
  };

  // Example JSON data
  $scope.data.qualifications = [
    { regime: 'FCA', requestedQualification: 'Unit 1 - UK Regulations', status: 'Pending', available: 'NO', showUploadSection: false },
    { regime: 'FCA', requestedQualification: 'Unit 2 - Securities', status: 'Pending', available: 'NO', showUploadSection: false },
    { regime: 'FCA', requestedQualification: 'Unit 3 - Derivatives', status: 'Pending', available: 'NO', showUploadSection: false }
    // Add more qualifications as needed
  ];

  $scope.toggleUploadSection = function(qualification) {
    qualification.showUploadSection = !qualification.showUploadSection;
  };
}

