sp_widget.client_script:
function($scope) {
  $scope.data = {
    qualifications: []
  };
  // Example JSON data
  $scope.data.qualifications = [
    { regime: 'FCA', requestedQualification: 'Unit 1 - UK Regulations', status: 'Pending', available: 'NO', showYesSection: false, showNoSection: false },
    { regime: 'FCA', requestedQualification: 'Unit 2 - Securities', status: 'Pending', available: 'NO', showYesSection: false, showNoSection: false },
    { regime: 'FCA', requestedQualification: 'Unit 3 - Derivatives', status: 'Pending', available: 'NO', showYesSection: false, showNoSection: false }
    // Add more qualifications as needed
  ];
  $scope.toggleYesSection = function (qualification) {
    qualification.showYesSection = !qualification.showYesSection;
    if (qualification.showYesSection) {
      qualification.showNoSection = false; // Close "No" section if "Yes" is opened
    }
  };

 $scope.toggleNoSection = function (qualification) {
  qualification.showNoSection = !qualification.showNoSection;
  if (qualification.showNoSection) {
    qualification.showYesSection = false;
  }
};


	$scope.triggerFileUpload = function () {
  document.getElementById('fileInput').click();
};

}

